import React, { useState } from "react";
import "./Chatbot.css";
const { Configuration, OpenAIApi } = require('openai');
const Chatbot = () => {
  const [messages, setMessages] = useState([{
    role: 'system',
    content: `
    first you should say send an article. don't say anything else
    You will classify the Sentiment of the article and word .
    there is only four type of Sentiment:
    Fear
    Anger
    Joy
    Sad
    and also the Positive ratio out of 5 and and negative out of 5 .
    if the user ask anything else say sorry i can only analyze the articles and words.
    Do not write explanations.
    Don't ask him if he need any help. you are an analyst
    `,
},
{
    role: 'assistant',
    content: 'content: I am ready to be an analyst. ',
}]);
  const [currentMessage, setCurrentMessage] = useState("");

  const handleChange = (event) => {
    setCurrentMessage(event.target.value);
  };
  console.log("messages1>>>>>>");
  console.log(messages);
  const handleSubmit = (event) => {
    event.preventDefault();
    console.log("messages2>>>>>>");
    console.log(messages);
    setMessages([...messages, { role: "user", content: currentMessage }]);
    setCurrentMessage("");
    // Add logic here to process the user's message and generate a response
    sendMessageToChatGPT()
  };
  async function sendMessageToChatGPT() {
	const configuration = new Configuration({
		apiKey: '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',
	});
	const openai = new OpenAIApi(configuration);

    console.log("messages3>>>>>>");
    console.log(messages);
	const completion = await openai.createChatCompletion({
		model: 'gpt-3.5-turbo',
		messages: messages,
		temperature: 0,
		max_tokens: 100,
	});
    setMessages([...messages, { role: "assistant", content: currentMessage }]);
    setCurrentMessage("");
}

  return (
    <div className="chatbot-container">
      <div className="chatbot-messages">
        {messages.map((message, index) => (
          <div
            key={index}
            className={message.role ? "user" : "assistant"}
          >
            {message.text}
          </div>
        ))}
      </div>
      <form className="chatbot-input-container" onSubmit={handleSubmit}>
        <input
          className="chatbot-input"
          type="text"
          placeholder="Type your message..."
          value={currentMessage}
          onChange={handleChange}
        />
        <button className="chatbot-send-button" type="submit">
          Send
        </button>
      </form>
    </div>
  );
};

export default Chatbot;
