import React, { useState } from "react";
import "./Chatbot.css";

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [currentMessage, setCurrentMessage] = useState("");

  const handleChange = (event) => {
    setCurrentMessage(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setMessages([...messages, { text: currentMessage, isUser: true }]);
    setCurrentMessage("");
    // Add logic here to process the user's message and generate a response
  };

  return (
    <div className="chatbot-container">
      <div className="chatbot-messages">
        {messages.map((message, index) => (
          <div
            key={index}
            className={message.isUser ? "chatbot-user-message" : "chatbot-bot-message"}
          >
            {message.text}
          </div>
        ))}
      </div>
      <form className="chatbot-input-container" onSubmit={handleSubmit}>
        <input
          className="chatbot-input"
          type="text"
          placeholder="Type your message..."
          value={currentMessage}
          onChange={handleChange}
        />
        <button className="chatbot-send-button" type="submit">
          Send
        </button>
      </form>
    </div>
  );
};

export default Chatbot;
